import { useEffect, useState } from 'react';
import { Box, Grid, LinearProgress, Paper, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import * as yup from 'yup';

import { OrdersService } from '../../shared/services/api/orders/OrdersService';
import { VTextField, VForm, useVForm, IVFormErrors } from '../../shared/forms';
import { DetailTool } from '../../shared/components';
import { LayoutBaseDePagina } from '../../shared/layouts';
import { AutoCompleteTechnician } from './components/AutoCompleteTechnician';
import { AutoCompleteEquipment } from './components/AutoCompleteEquipment';


interface IFormData {
  equipmentID: number,
  technicianID: number,
  type: string,
  defect: string,
  causes: string,
  solution: string,
  date_init_os: Date,
  date_end_os?: Date,
  total: number,
}
const formValidationSchema: yup.SchemaOf<IFormData> = yup.object().shape({
  type: yup.string().required().min(5).max(20),
  defect: yup.string().required().min(3).max(150),
  causes: yup.string().required().min(3).max(150),
  solution: yup.string().required().min(3).max(150),
  date_init_os: yup.date().required(),
  date_end_os: yup.date().optional(),
  total: yup.number().required(),
  equipmentID: yup.number().integer().required(),
  technicianID: yup.number().integer().required(),
});

export const DetailOrders: React.FC = () => {
  const { formRef, save, saveAndClose, isSaveAndClose } = useVForm();
  const { id = 'nova' } = useParams<'id'>();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [defect, setDefect] = useState('');


  useEffect(() => {
    if (id !== 'nova') {
      setIsLoading(true);

      OrdersService.getById(Number(id))
        .then((result) => {
          setIsLoading(false);

          if (result instanceof Error) {
            alert(result.message);
            navigate('/orders');
          } else {
            setDefect(result.defect);
            formRef.current?.setData(result);
          }
        });
    } else {
      formRef.current?.setData({
        setName: '',
      });
    }
  }, [id]);


  const handleSave = (dados: IFormData) => {
    formValidationSchema.
      validate(dados, { abortEarly: false })
      .then((dadosValidados) => {
        setIsLoading(true);

        if (id === 'nova') {
          OrdersService
            .create(dadosValidados)
            .then((result) => {
              setIsLoading(false);

              if (result instanceof Error) {
                alert(result.message);
              } else {
                if (isSaveAndClose()) {
                  navigate('/orders');
                } else {
                  navigate(`/orders/detail/${result}`);
                }
              }
            });
        } else {
          OrdersService
            .updateById(Number(id), { id: Number(id), ...dadosValidados })
            .then((result) => {
              setIsLoading(false);

              if (result instanceof Error) {
                alert(result.message);
              } else {
                if (isSaveAndClose()) {
                  navigate('/orders');
                }
              }
            });
        }
      })
      .catch((errors: yup.ValidationError) => {
        const validationErrors: IVFormErrors = {};

        errors.inner.forEach(error => {
          if (!error.path) return;

          validationErrors[error.path] = error.message;
        });

        formRef.current?.setErrors(validationErrors);
      });
  };

  const handleDelete = (id: number) => {
    if (confirm('Realmente deseja apagar?')) {
      OrdersService.deleteById(id)
        .then(result => {
          if (result instanceof Error) {
            alert(result.message);
          } else {
            alert('Registro apagado com sucesso!');
            navigate('/orders');
          }
        });
    }
  };


  return (
    <LayoutBaseDePagina
      titulo={id === 'nova' ? 'New Order' : defect}
      barraDeFerramentas={
        <DetailTool
          textoBotaoNovo='Nova'
          mostrarBotaoSalvarEFechar
          mostrarBotaoNovo={id !== 'nova'}
          mostrarBotaoApagar={id !== 'nova'}

          aoClicarEmSalvar={save}
          aoClicarEmSalvarEFechar={saveAndClose}
          aoClicarEmVoltar={() => navigate('/orders')}
          aoClicarEmApagar={() => handleDelete(Number(id))}
          aoClicarEmNovo={() => navigate('/orders/detail/nova')}
        />
      }
    >
      <VForm ref={formRef} onSubmit={handleSave}>
        <Box margin={1} display="flex" flexDirection="column" component={Paper} variant="outlined">

          <Grid container direction="column" padding={2} spacing={2}>

            {isLoading && (
              <Grid item>
                <LinearProgress variant='indeterminate' />
              </Grid>
            )}

            <Grid item>
              <Typography variant='h6'>Register Order</Typography>
            </Grid>
            <Grid container item direction="row" spacing={2}>

              <Grid container item direction="column" spacing={2}>
                <Grid item xs={12} sm={12} md={6} lg={4} xl={2}>
                  <AutoCompleteEquipment isExternalLoading={isLoading} />
                </Grid>
              </Grid>

              <Grid container item direction="column" spacing={2}>
                <Grid item xs={12} sm={12} md={6} lg={4} xl={2}>
                  <AutoCompleteTechnician isExternalLoading={isLoading} />
                </Grid>
              </Grid>

            </Grid>

            <Grid item direction="row">
              <Grid item xs={12} sm={12} md={6} lg={4} xl={3}>
                <VTextField
                  fullWidth
                  name='defect'
                  label='Defeito'
                  disabled={isLoading}
                  onChange={e => setDefect(e.target.value)}
                />
              </Grid>
            </Grid>
            <Grid item direction="row">
              <Grid item xs={12} sm={12} md={6} lg={4} xl={2}>
                <VTextField
                  fullWidth
                  name='causes'
                  label='Causa'
                  disabled={isLoading}
                />
              </Grid>
            </Grid>

            <Grid container item direction="row" spacing={2}>
              <Grid item xs={12} sm={12} md={6} lg={4} xl={3}>
                <VTextField
                  fullWidth
                  name='solution'
                  label='Solução'
                  disabled={isLoading}
                />
              </Grid>
            </Grid>
            <Grid container item direction="row" spacing={2}>
              <Grid item direction="column">
                <Grid item xs={12} sm={12} md={6} lg={4} xl={3}>
                  <VTextField
                    fullWidth
                    name='type'
                    label='Tipo'
                    disabled={isLoading}
                  />
                </Grid>
              </Grid>
              <Grid item direction="column">
                <Grid item xs={12} sm={12} md={6} lg={4} xl={3}>
                  <VTextField
                    fullWidth
                    name='date_init_os'
                    label='Data Inicial'
                    disabled={isLoading}
                  />
                </Grid>
              </Grid>
              <Grid item direction="column">
                <Grid item xs={12} sm={12} md={6} lg={4} xl={2}>
                <DateField label="Uncontrolled field" defaultValue={dayjs('2022-04-17')} />
                  <VTextField
                    fullWidth
                    name='date_end_os'
                    label='Data final'
                    disabled={isLoading}
                  />
                </Grid>
              </Grid>
            </Grid>
            <Grid container item direction="row" spacing={2}>
              <Grid item xs={12} sm={12} md={8} lg={6} xl={4}>
                <VTextField
                  fullWidth
                  name='total'
                  label='Total'
                  disabled={isLoading}
                />
              </Grid>
            </Grid>



          </Grid>

        </Box>
      </VForm>
    </LayoutBaseDePagina >
  );
};
