import { useEffect, useMemo, useState } from 'react';
import { Autocomplete, CircularProgress, TextField } from '@mui/material';

import { TechniciansService } from '../../../shared/services/api/technicians/TechniciansService';
import { useDebounce } from '../../../shared/hooks';
import { useField } from '@unform/core';


type TAutoCompleteOption = {
  id: number;
  label: string;
}

interface IAutoCompleteTechnicianProps {
  isExternalLoading?: boolean;
}
export const AutoCompleteTechnician: React.FC<IAutoCompleteTechnicianProps> = ({ isExternalLoading = false }) => {
  const { fieldName, registerField, defaultValue, error, clearError } = useField('id');
  const { debounce } = useDebounce();

  const [selectedId, setSelectedId] = useState<number | undefined>(defaultValue);

  const [opcoesTechnician, setOpcoesTechnician] = useState<TAutoCompleteOption[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [busca, setBusca] = useState('');

  useEffect(() => {
    registerField({
      name: fieldName,
      getValue: () => selectedId,
      setValue: (_, newSelectedId) => setSelectedId(newSelectedId),
    });
  }, [registerField, fieldName, selectedId]);

  useEffect(() => {
    setIsLoading(true);

    debounce(() => {
      TechniciansService.getAll(1, busca, selectedId)
        .then((result) => {
          setIsLoading(false);

          if (result instanceof Error) {
            // alert(result.message);
          } else {
            console.log(result);

            setOpcoesTechnician(result.data.map(technician => ({ id: technician.id, label: technician.name })));
          }
        });
    });
  }, [busca, selectedId]);

  const autoCompleteTechnicianSelectedOption = useMemo(() => {
    if (!selectedId) return null;

    const selectedOption = opcoesTechnician.find(opcoesTechnician => opcoesTechnician.id === selectedId);
    if (!selectedOption) return null;

    return selectedOption;
  }, [selectedId, opcoesTechnician]);


  return (
    <Autocomplete
      openText='Abrir'
      closeText='Fechar'
      noOptionsText='Sem opções'
      loadingText='Carregando...'

      disablePortal

      options={opcoesTechnician}
      loading={isLoading}
      disabled={isExternalLoading}
      value={autoCompleteTechnicianSelectedOption}
      onInputChange={(_, newValue) => setBusca(newValue)}
      onChange={(_, newValue) => { setSelectedId(newValue?.id); setBusca(''); clearError(); }}
      popupIcon={(isExternalLoading || isLoading) ? <CircularProgress size={28} /> : undefined}
      renderInput={(params) => (
        <TextField
          {...params}

          label="Técnico"
          error={!!error}
          helperText={error}
        />
      )}
    />
  );
};
