import {
  APIProvider,
  Map,
  useMap,
  useMapsLibrary,
} from '@vis.gl/react-google-maps';
import { Environment } from '../../../shared/environment';
import { useEffect, useState } from 'react';
import {
  IListClient,
  ClientsService,
} from '../../../shared/services/api/clients/ClientsService';
import { useDebounce } from '../../../shared/hooks';

export const MapsDashboard: React.FC = () => {
  const position = { lat: 43.6532, lng: -79.3832 };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <APIProvider apiKey={Environment.API_KEY}>
        <Map
          center={position}
          defaultZoom={9}
          mapId={Environment.ID_MAP}
          fullscreenControl={false}
        >
          <Directions />
        </Map>
      </APIProvider>
    </div>
  );
};

function Directions() {
  const { debounce } = useDebounce();
  //const [rows, setRows] = useState<IListClient[]>([]);
  const map = useMap();
  const routesLibrary = useMapsLibrary('routes');
  const [directionsService, setDirectionsService] =
    useState<google.maps.DirectionsService>();
  const [directionsRenderer, setDirectionsRenderer] =
    useState<google.maps.DirectionsRenderer>();
  const [routes, setRoutes] = useState<google.maps.DirectionsRoute[]>([]);
  const [routeIndex, setRouteIndex] = useState(0);
  const selected = routes[routeIndex];
  const leg = selected?.legs[0];

  const originDestination = {
    lat: localStorage.getItem('lat'),
    lng: localStorage.getItem('long'),
  };

  useEffect(() => {
    if (!routesLibrary || !map) return;
    setDirectionsService(new routesLibrary.DirectionsService());
    setDirectionsRenderer(new routesLibrary.DirectionsRenderer({ map }));
  }, [routesLibrary, map]);

  useEffect(() => {
    if (!directionsService || !directionsRenderer) return;

    debounce(() => {
      ClientsService.getAll().then((result) => {
        if (result instanceof Error) {
          alert(result.message);
        } else {
          const locations = result.data;
          console.log(locations);
          const waypoints = locations.map((location) => ({
            location: { lat: location.latitude, lng: location.longitude },
            stopover: true,
          }));

          console.log(waypoints);
          if (!originDestination) return;
          directionsService
            .route({
              origin: originDestination,
              destination: originDestination,
              waypoints: waypoints,
              travelMode: google.maps.TravelMode.DRIVING,
              provideRouteAlternatives: true,
            })
            .then((response) => {
              directionsRenderer.setDirections(response);
              setRoutes(response.routes);
            });
        }
      });
    });
  }, [directionsService, directionsRenderer]);

  useEffect(() => {
    if (!directionsRenderer) return;
    directionsRenderer.setRouteIndex(routeIndex);
  }, [routeIndex, directionsRenderer]);

  if (!leg) return null;
  // console.log(originDestination);
  return (
    <div
      style={{
        boxShadow: '0 0 4px rgba(0,0,0,.15)',
        backgroundColor: 'black',
        color: 'white',
        cursor: 'auto',
        fontSize: '14px',
        lineHeight: '18px',
        outline: '0',
        padding: '12px 24px',
        position: 'absolute',
        right: '0',
        top: '0',
        width: '284px',
      }}
    >
      <h2>{selected.summary}</h2>
      <p>
        {leg.start_address.split(',')[0]} para {leg.end_address.split(',')[0]}
      </p>
      <p>Distancia: {leg.distance?.text}</p>
      <p>Duração: {leg.duration?.text}</p>

      <h2>Outras rotas:</h2>
      <ul>
        {routes.map((route, index) => (
          <li key={route.summary}>
            <button onClick={() => setRouteIndex(index)}>
              {route.summary}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
