import { useEffect, useState } from 'react';
import { Box, Card, CardContent, Grid, Typography } from '@mui/material';

import { ClientsService } from '../../shared/services/api/clients/ClientsService';
import { ToolsListing } from '../../shared/components';
import { LayoutBaseDePagina } from '../../shared/layouts';
import { ListClientsDashboard } from './components/ListClients';


export const Dashboard = () => {
  const [isLoadingClients, setIsLoadingClients] = useState(true);
  const [totalCountClients, setTotalCountClients] = useState(0);

  useEffect(() => {
    setIsLoadingClients(true);

    ClientsService.getAll(1)
      .then((result) => {
        setIsLoadingClients(false);

        if (result instanceof Error) {
          alert(result.message);
        } else {
          setTotalCountClients(result.totalCount);
        }
      });

  }, []);


  return (
    <LayoutBaseDePagina
      titulo='PÃ¡gina inicial'
      barraDeFerramentas={<ToolsListing mostrarBotaoNovo={false} />}
    >
      <Box width='100%' display='flex'>
        <Grid container margin={2}>
          <Grid item container spacing={2}>

            <Grid item xs={12} sm={12} md={6} lg={4} xl={3}>

              <Card>
                <CardContent>
                  <Typography variant='h6' align='left'>
                    Tecnicos:
                  </Typography>

                  <Box padding={1} display='flex' justifyContent='center' alignItems='center'>
                    {!isLoadingClients && (
                      <Typography variant='h4'>
                        {totalCountClients}
                      </Typography>
                    )}
                    {isLoadingClients && (
                      <Typography variant='h6'>
                        Carregando...
                      </Typography>
                    )}
                  </Box>
                </CardContent>
              </Card>

            </Grid>

          </Grid>

          <ListClientsDashboard />


        </Grid>
      </Box>
    </LayoutBaseDePagina>
  );
};
