import * as React from 'react';
import { useEffect, useState } from 'react';
import dayjs, { Dayjs } from 'dayjs';
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DatePicker, DatePickerProps } from '@mui/x-date-pickers/DatePicker';
import { TextFieldProps } from '@mui/material';
import { useField } from '@unform/core';

//type TVTextFieldProps = DatePickerProps<any> & {
//  name: string;
//}
type TVTextFieldProps = Omit<DatePickerProps<any>, 'value'> & Omit<TextFieldProps, 'value'> & {
  name: string;

  onValueChange?: (value: string) => void;
}

//export const VDateField = () => {
//const [value, setValue] = React.useState<Dayjs | null>(dayjs('2024-01-19T03:00:00.000Z'));

export const VDateField: React.FC<TVTextFieldProps> = ({ name, ...rest }) => {

  const { fieldName, registerField, defaultValue} = useField(name);

  const [value, setValue] = useState<Dayjs | null>(defaultValue || null);
  //const [value, setValue] = useState<Dayjs | null>(dayjs('2024-01-19T03:00:00.000Z'));

  useEffect(() => {
    registerField({
      name: fieldName,
      getValue: () => value,
      setValue: (_, value) => setValue(value),
    });
  }, [registerField, fieldName, value]);

  console.log(value);
  const handleChange = (value: string) => {
    setValue(value);
    onValueChange && onValueChange(value);
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DemoContainer components={['DatePicker']}>
        <DatePicker
          {...rest}

          label="Controlled picker"
          value={value}
          onValueChange={({ value }) => handleChange(value)}
          //onChange={e => { setValue(e.target.value); rest.onChange?.(e); }}
          //onKeyDown={(e) => { error && clearError(); rest.onKeyDown?.(e); }}
          //onChange={(newValue) => setValue(newValue)}
        />
      </DemoContainer>
    </LocalizationProvider>
  );
};
