import { Grid, Paper } from '@mui/material';
import {
  APIProvider,
  InfoWindow,
  Map,
  Marker,
  useMap,
  useMapsLibrary,
} from '@vis.gl/react-google-maps';
import { Environment } from '../../../shared/environment';
import { useEffect, useState } from 'react';

export const MapsDashboard: React.FC = () => {
  const position = { lat: 43.6532, lng: -79.3832 };
  const [open, setOpen] = useState(false);

  return (
    <>
      <APIProvider apiKey={Environment.API_KEY}>
        <Map center={position} defaultZoom={9} mapId={Environment.ID_MAP}>
          {/* <Marker position={position} onClick={() => setOpen(true)} />
          {open && (
            <InfoWindow position={position} onCloseClick={() => setOpen(false)}>
              <p>Cliente 1</p>
            </InfoWindow>
          )} */}
          <Directions />
        </Map>
      </APIProvider>
    </>
  );
};

function Directions() {
  const map = useMap();
  const routesLibrary = useMapsLibrary('routes');
  const [directionsService, setDirectionsService] =
    useState<google.maps.DirectionsService>();
  const [directionsRenderer, setDirectionsRenderer] =
    useState<google.maps.DirectionsRenderer>();
  const [routes, setRoutes] =
    useState<google.maps.DirectionsRoute[]>([]);
    const [routeIndex, setRouteIndex] =
    useState(0);

  useEffect(() => {
    if (!routesLibrary || !map) return;
    setDirectionsService(new routesLibrary.DirectionsService());
    setDirectionsRenderer(new routesLibrary.DirectionsRenderer({ map }));
  }, [routesLibrary, map]);

  useEffect(() => {
    if (!directionsService || !directionsRenderer) return;
    directionsService
      .route({
        origin: '100 Front St, Toronto ON',
        destination: '500 College St, Toronto ON',
        travelMode: google.maps.TravelMode.DRIVING,
        provideRouteAlternatives: true,
      }).then(response => {
        directionsRenderer.setDirections(response);
        setRoutes(response.routes);
      });
  }, [directionsService, directionsRenderer]);

  console.log(routes);

  return null;
}
