import React, { useState } from 'react';
import './styles.css';

import { Grid, TextField, TextFieldProps } from '@mui/material';
import * as yup from 'yup';
import { formValidationSchema } from './DetailItemofLines';
import {
  IListItemofLine,
  ItemofLinesService,
} from '../../../../shared/services/api/itemofLines/ItemofLinesService';
import {
  IVFormErrors,
  VForm,
  VTextField,
  useVForm,
} from '../../../../shared/forms';
import { useNavigate, useParams } from 'react-router-dom';
import { AutoCompleteItem } from './AutoCompleteItem';

type TVTextFieldProps = {
  name: string;
  data: IListItemofLine;
};

interface IFormData {
  orderID: number;
  itemID: number;
  amount: number;
  total: number;
}

const [changedAmount, setChangedAmount] = useState(data.amount);
const [changedTotal, setChangedTotal] = useState(data.total);
const [changedItemID, setChangedItemID] = useState(data.itemID);
const [newdata, setNewData] = useState([]);
const [isLoading, setIsLoading] = useState(false);
const { formRef, save, saveAndClose, isSaveAndClose } = useVForm();
const { id = 'nova' } = useParams<'id'>();
const navigate = useNavigate();
const [rows, setRows] = useState<IListItemofLine[]>([]);
const [totalCount, setTotalCount] = useState(0);

/* async function handleSave(e, notes) {
        e.style.cursor = 'default';
        e.style.boxShadow = 'none';
        if (changedNote && changedNote !== notes) {
            await api.post(`/contents/${data._id}`, {
                notes: changedNote,
            });
        }
    } */
const upgrade = (dados: IFormData) => {
  console.log(changedAmount);
  console.log(changedTotal);
};

const handleSave = (dados: IFormData) => {
  formValidationSchema
    .validate(dados, { abortEarly: false })
    .then((dadosValidados) => {
      setIsLoading(true);
      if (
        changedAmount !== dados.amount ||
        changedAmount !== dados.total ||
        changedAmount !== dados.itemID
      ) {
        ItemofLinesService.updateById(Number(id), {
          id: Number(id),
          ...dadosValidados,
          amount: changedAmount,
          total: changedTotal,
          itemID: changedItemID,
        }).then((result) => {
          setIsLoading(false);

          if (result instanceof Error) {
            alert(result.message);
          } else {
            if (isSaveAndClose()) {
              navigate('/itemofLines');
            }
          }
        });
      }
    })
    .catch((errors: yup.ValidationError) => {
      const validationErrors: IVFormErrors = {};

      errors.inner.forEach((error) => {
        if (!error.path) return;

        validationErrors[error.path] = error.message;
      });

      formRef.current?.setErrors(validationErrors);
    });
};

const handleDelete = (id: number) => {
  if (confirm('Realmente deseja apagar?')) {
    ItemofLinesService.deleteById(id).then((result) => {
      if (result instanceof Error) {
        alert(result.message);
      } else {
        alert('Registro apagado com sucesso!');
        navigate('/itemofLines');
      }
    });
  }
};

/* async function handleEdit(e, priority) {
    e.style.cursor = 'text';
    e.style.borderRadius = '5px';
    if (priority) {
      e.style.boxShadow = '0 0 5px white';
    } else {
      e.style.boxShadow = '0 0 5px gray';
    }
  } */
export const Notes: React.FC<TVTextFieldProps> = ({ data }) => {
  return (
    <VForm key={data.id} ref={formRef} onSubmit={handleSave}>
      <Grid
        container
        direction="row"
        justifyContent="space-between"
        alignItems="center"
        borderBottom={1}
        spacing={{ xs: 0, md: 0 }}
        columns={{ xs: 3, sm: 6, md: 8 }}
      >
        <Grid item xs={2} sm={4} md={3}>
          <AutoCompleteItem
            isExternalLoading={isLoading}
            itemID={data.itemID}
          />
        </Grid>

        <Grid item xs={2} sm={4} md={2}>
          <TextField
            variant="standard"
            defaultValue={data.amount}
            /* onClick={(e) => handleEdit(e.target, data.priority)} */
            onChange={(e) => setChangedAmount(Number(e.target.value))}
            onBlur={(e) => upgrade(data)}
          />
        </Grid>
        <Grid item xs={2} sm={4} md={2}>
          <TextField
            variant="standard"
            defaultValue={data.total}
            /* onClick={(e) => handleEdit(e.target, data.priority)} */
            onChange={(e) => setChangedTotal(Number(e.target.value))}
            /*  onBlur={(e) => handleSave(e.target, data.total)} */
            onBlur={(e) => upgrade(data)}
          />
        </Grid>
      </Grid>
    </VForm>
  );
};

export default Notes;
