import { useEffect, useState } from 'react';
import {
  Box,
  Grid,
  LinearProgress,
  Paper,
  Typography,
  debounce,
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import * as yup from 'yup';

import {
  VTextField,
  VForm,
  useVForm,
  IVFormErrors,
} from '../../../../shared/forms';
import { DetailTool } from '../../../../shared/components';
import { LayoutBaseDePagina } from '../../../../shared/layouts';
import {
  IListItemofLine,
  ItemofLinesService,
} from '../../../../shared/services/api/itemofLines/ItemofLinesService';
import TableList from './list';
import { useDebounce } from '../../../../shared/hooks';
import { AutoCompleteItem } from './AutoCompleteItem';

interface IFormData {
  orderID: number;
  itemID: number;
  amount: number;
  total: number;
}
export const formValidationSchema: yup.SchemaOf<IFormData> = yup
  .object()
  .shape({
    amount: yup.number().required(),
    total: yup.number().required(),
    orderID: yup.number().integer().required(),
    itemID: yup.number().integer().required(),
  });

export const DetailItemofLines: React.FC = () => {
  const { debounce } = useDebounce();
  const { formRef, save, saveAndClose, isSaveAndClose } = useVForm();
  const { id } = useParams<'id'>();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [rows, setRows] = useState<IListItemofLine[]>([]);
  const [totalCount, setTotalCount] = useState(0);

  /* useEffect(() => {
    if (id !== 'nova') {
      setIsLoading(true);

      ItemofLinesService.getById(Number(id)).then((result) => {
        setIsLoading(false);

        if (result instanceof Error) {
          alert(result.message);
          navigate('/itemofLines');
        } else {
          formRef.current?.setData(result);
        }
      });
    } else {
      formRef.current?.setData({
        setName: '',
        setSerieNumber: '',
        setType: '',
        setDescription: '',
      });
    }
  }, [id]); */

  useEffect(() => {
    setIsLoading(true);
    debounce(() => {
      ItemofLinesService.getAll(1, id).then((result) => {
        setIsLoading(false);

        if (result instanceof Error) {
          alert(result.message);
        } else {
          setTotalCount(result.totalCount);
          setRows(result.data);
        }
      });
    });
  }, [id]);

  const handleSave = (dados: IFormData) => {
    formValidationSchema
      .validate(dados, { abortEarly: false })
      .then((dadosValidados) => {
        setIsLoading(true);
        ItemofLinesService.create(dadosValidados).then((result) => {
          setIsLoading(false);

          if (result instanceof Error) {
            alert(result.message);
          } else {
            if (isSaveAndClose()) {
              navigate('/itemofLines');
            } else {
              navigate(`/itemofLines/detail/${result}`);
            }
          }
        });
      })
      .catch((errors: yup.ValidationError) => {
        const validationErrors: IVFormErrors = {};

        errors.inner.forEach((error) => {
          if (!error.path) return;

          validationErrors[error.path] = error.message;
        });

        formRef.current?.setErrors(validationErrors);
      });
  };

  const handleDelete = (id: number) => {
    if (confirm('Realmente deseja apagar?')) {
      ItemofLinesService.deleteById(id).then((result) => {
        if (result instanceof Error) {
          alert(result.message);
        } else {
          alert('Registro apagado com sucesso!');
          navigate('/itemofLines');
        }
      });
    }
  };
  return (
    <LayoutBaseDePagina
      titulo={id === 'nova' ? 'New Item' : ''}
      barraDeFerramentas={
        <DetailTool
          textoBotaoNovo="Nova"
          mostrarBotaoSalvarEFechar
          mostrarBotaoNovo={id !== 'nova'}
          mostrarBotaoApagar={id !== 'nova'}
          aoClicarEmSalvar={save}
          aoClicarEmSalvarEFechar={saveAndClose}
          aoClicarEmVoltar={() => navigate('/itemofLines')}
          aoClicarEmApagar={() => handleDelete(Number(id))}
          aoClicarEmNovo={() => navigate('/itemofLines/detail/nova')}
        />
      }
    >
      <VForm key={id} ref={formRef} onSubmit={handleSave}>
        <Box
          margin={1}
          display="flex"
          flexDirection="column"
          component={Paper}
          variant="outlined"
        >
          <Grid container direction="column" padding={2} spacing={2}>
            {isLoading && (
              <Grid item>
                <LinearProgress variant="indeterminate" />
              </Grid>
            )}

            <Grid item>
              <Typography variant="h6">Register ItemofLine</Typography>
            </Grid>

            <Grid
              container
              item
              spacing={{ xs: 2, md: 3 }}
              columns={{ xs: 3, sm: 6, md: 8 }}
            >
              <Grid item xs={12}>
                <DetailTool
                  textoBotaoNovo="Nova"
                  mostrarBotaoSalvarEFechar
                  mostrarBotaoNovo={id !== 'nova'}
                  mostrarBotaoApagar={id !== 'nova'}
                  aoClicarEmSalvar={save}
                  aoClicarEmSalvarEFechar={saveAndClose}
                  aoClicarEmVoltar={() => navigate('/itemofLines')}
                  aoClicarEmApagar={() => handleDelete(Number(id))}
                  aoClicarEmNovo={() => navigate('/itemofLines/detail/nova')}
                />
              </Grid>

              <Grid item xs={2} sm={3} md={2} marginTop={2}>
                <AutoCompleteItem isExternalLoading={isLoading} itemID={0} />
              </Grid>
              <Grid item xs={2} sm={3} md={2}>
                <VTextField
                  variant="standard"
                  fullWidth
                  name="amount"
                  label="Quantidade"
                  disabled={isLoading}
                />
              </Grid>
              <Grid item xs={4} sm={6} md={4}>
                <VTextField
                  variant="standard"
                  fullWidth
                  name="total"
                  label="Total"
                  disabled={isLoading}
                />
              </Grid>
            </Grid>
          </Grid>
        </Box>
      </VForm>
      <Box
        margin={1}
        display="flex"
        flexDirection="column"
        component={Paper}
        variant="outlined"
      >
        <Grid container direction="column" padding={2} spacing={2}>
          <Grid
            container
            item
            spacing={{ xs: 2, md: 3 }}
            columns={{ xs: 3, sm: 6, md: 8 }}
          ></Grid>
        </Grid>
      </Box>
      <Box
        sx={{ flexGrow: 1, p: 2 }}
        margin={1}
        display="flex"
        flexDirection="column"
        component={Paper}
        variant="outlined"
      >
        <Grid container direction="column" margin={2} padding={2} spacing={2}>
          <Grid
            container
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            marginBottom={2}
            borderBottom={1}
            spacing={{ xs: 2, md: 3 }}
            columns={{ xs: 3, sm: 6, md: 8 }}
          >
            <Grid item xs={2} sm={4} md={3}>
              <Typography variant="h6">Produto/Serviço</Typography>
            </Grid>
            <Grid item xs={2} sm={4} md={2}>
              <Typography variant="h6">Qtd</Typography>
            </Grid>
            <Grid item xs={2} sm={4} md={2}>
              <Typography variant="h6">Total</Typography>
            </Grid>
          </Grid>
          {rows.map((row) => (
            <TableList key={row.id} id={row.id} name="item" data={row} />
          ))}
        </Grid>
      </Box>
    </LayoutBaseDePagina>
  );
};
