import { useEffect, useState } from 'react';
import { Grid, Icon, IconButton, LinearProgress, Paper, Table, TableBody, TableCell, TableContainer, TableFooter, TableHead, TableRow, TextFieldProps } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';

import { IListItemofLine, ItemofLinesService, } from '../../../../shared/services/api/itemofLines/ItemofLinesService';
import { Environment } from '../../../../shared/environment';
import { useDebounce } from '../../../../shared/hooks';

type TVTextFieldProps = TextFieldProps & {
  name: string;
}

export const ListItemofLines: React.FC<TVTextFieldProps> = () => {

  const { debounce } = useDebounce();
  const navigate = useNavigate();

  const [rows, setRows] = useState<IListItemofLine[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalCount, setTotalCount] = useState(0);

  const { id } = useParams<'id'>();



  useEffect(() => {
    setIsLoading(true);
    debounce(() => {
      ItemofLinesService.getAll(1, id)
        .then((result) => {
          setIsLoading(false);

          if (result instanceof Error) {
            alert(result.message);
          } else {

            setTotalCount(result.totalCount);
            setRows(result.data);
          }
        });
    });
  }, [id]);

  const handleDelete = (id: number) => {
    if (confirm('Realmente deseja apagar?')) {
      ItemofLinesService.deleteById(id)
        .then(result => {
          if (result instanceof Error) {
            alert(result.message);
          } else {
            setRows(oldRows => [
              ...oldRows.filter(oldRow => oldRow.id !== id),
            ]);
            alert('Registro apagado com sucesso!');
          }
        });
    }
  };
  return (
    <>
      <Grid container item xs={12} marginTop={2} component={Paper} variant="outlined" >
        <Grid item xs="auto">
          Nome
        </Grid>
        <Grid item xs="auto">
          Quantidade
        </Grid>
        <Grid item xs="auto">
          Total
        </Grid>
      </Grid>
      {rows.map(row => (
        <Grid container item xs={12} marginTop={2} component={Paper} variant="outlined" key={row.id}>
          <Grid item xs="auto">
            Nome
          </Grid>
          <Grid item xs="auto">
            Quantidade
          </Grid>
          <Grid item xs="auto">
            Total
          </Grid>
        </Grid>
      ))}

      {totalCount === 0 && !isLoading && (
        <caption>{Environment.EMPTY_LISTING}</caption>
      )}

      {rows.map(row => (
        <TableRow key={row.id}>
          <TableCell>
            <IconButton size="small" onClick={() => handleDelete(row.id)}>
              <Icon>delete</Icon>
            </IconButton>
            <IconButton size="small" onClick={() => navigate(`/itemofLines/detail/${row.id}`)}>
              <Icon>edit</Icon>
            </IconButton>
          </TableCell>
          <TableCell>{row.itemID}</TableCell>
          <TableCell>{row.amount}</TableCell>
          <TableCell>{row.total}</TableCell>
        </TableRow>
      ))}





    </>
  );
};
